// src/pages/Home.tsx
import React, { useState } from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import LoginModal from '../components/LoginModal';
import ProfileModal from '../components/ProfileModal';
import HomeContent from '../components/HomeContent';
import { useAuthStore } from '../store/authStore';

const HomeContainer = styled.div`
  padding-top: 50px; /* Ajuste conforme a altura do cabeçalho */
`;

const Home: React.FC = () => {
  const [isLoginModalOpen, setLoginModalOpen] = useState(false);
  const [isProfileModalOpen, setProfileModalOpen] = useState(false);
  const { isAuthenticated, setAuthenticated } = useAuthStore();

  const handleLoginClick = () => setLoginModalOpen(true);
  const handleProfileClick = () => setProfileModalOpen(true);
  const handleLogoutClick = () => setAuthenticated(false);

  return (
    <HomeContainer>
      <Header
        onLoginClick={handleLoginClick}
        onProfileClick={handleProfileClick}
        onLogoutClick={handleLogoutClick}
      />
      <HomeContent />
      
      {isLoginModalOpen && !isAuthenticated && (
        <LoginModal onClose={() => setLoginModalOpen(false)} />
      )}
      {isProfileModalOpen && isAuthenticated && (
        <ProfileModal onClose={() => setProfileModalOpen(false)} />
      )}
    </HomeContainer>
  );
};

export default Home;
