import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuthStore } from '../store/authStore';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
  margin-top: 10px;
`;

const DeleteButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #d9534f;
  color: #fff;
  cursor: pointer;
  margin-top: 10px;
`;

const ProfileModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const { userId, setAuthenticated } = useAuthStore();
  const [userData, setUserData] = useState({ name: '', email: '' });
  const [newName, setNewName] = useState(userData.name);
  const [newEmail, setNewEmail] = useState(userData.email);

  useEffect(() => {
    // Fetch user data
    const fetchUserData = async () => {
      try {
        const response = await axios.get(`/api/users/${userId}`);
        setUserData(response.data);
        setNewName(response.data.name);
        setNewEmail(response.data.email);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };
    fetchUserData();
  }, [userId]);

  const handleUpdate = async () => {
    try {
      await axios.put(`/api/users/${userId}`, { name: newName, email: newEmail });
      alert('Informações atualizadas com sucesso!');
      onClose();
    } catch (error) {
      alert('Erro ao atualizar informações.');
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/users/${userId}`);
      alert('Conta deletada com sucesso!');
      setAuthenticated(false); // Assuming you handle authentication state in a global store
      onClose();
    } catch (error) {
      alert('Erro ao deletar conta.');
    }
  };

  return (
    <ModalContainer>
      <ModalHeader>
        <ModalTitle>Seu Perfil</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      <InputGroup>
        <Label>Nome completo</Label>
        <Input value={newName} onChange={(e) => setNewName(e.target.value)} />
      </InputGroup>
      <InputGroup>
        <Label>E-mail</Label>
        <Input value={newEmail} onChange={(e) => setNewEmail(e.target.value)} />
      </InputGroup>
      <ActionButton onClick={handleUpdate}>Atualizar Informações</ActionButton>
      <DeleteButton onClick={handleDelete}>Deletar Conta</DeleteButton>
    </ModalContainer>
  );
};

export default ProfileModal;
