import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import { useAuthStore } from '../store/authStore';

const ModalContainer = styled.div`
  /* Estilos do modal */
`;

const InputGroup = styled.div`
  /* Estilos do grupo de input */
`;

const ActionButton = styled.button`
  /* Estilos do botão de ação */
`;

const ProfileModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const userId = useAuthStore((state) => state.userId); // Assume que userId é number
  const [userInfo, setUserInfo] = useState<{ name: string; email: string } | null>(null);

  useEffect(() => {
    if (userId) {
      const fetchUserInfo = async () => {
        try {
          const response = await axios.get(`http://localhost:5002/api/users/${userId}`);
          setUserInfo(response.data);
        } catch (error) {
          console.error('Error fetching user:', error);
        }
      };

      fetchUserInfo();
    }
  }, [userId]);

  const updateUser = async (userInfo: { name: string; email: string }) => {
    if (userId) {
      try {
        await axios.put(`http://localhost:5002/api/users/${userId}`, userInfo);
        alert('User updated successfully');
      } catch (error) {
        console.error('Error updating user:', error);
        alert('Error updating user');
      }
    }
  };

  const deleteUser = async () => {
    if (userId) {
      try {
        await axios.delete(`http://localhost:5002/api/users/${userId}`);
        alert('User deleted successfully');
        onClose(); // Close the modal after deletion
      } catch (error) {
        console.error('Error deleting user:', error);
        alert('Error deleting user');
      }
    }
  };

  if (!userId || !userInfo) {
    return <div>Loading...</div>;
  }

  return (
    <ModalContainer>
      <InputGroup>
        <label>Name:</label>
        <input
          type="text"
          value={userInfo.name}
          onChange={(e) => setUserInfo({ ...userInfo, name: e.target.value })}
        />
      </InputGroup>
      <InputGroup>
        <label>Email:</label>
        <input
          type="email"
          value={userInfo.email}
          onChange={(e) => setUserInfo({ ...userInfo, email: e.target.value })}
        />
      </InputGroup>
      <ActionButton onClick={() => updateUser(userInfo)}>Save changes</ActionButton>
      <ActionButton onClick={deleteUser} style={{ backgroundColor: '#d9534f' }}>
        Delete Account
      </ActionButton>
    </ModalContainer>
  );
};

export default ProfileModal;
