import React from 'react';
import styled from 'styled-components';
import { useAuthStore } from '../store/authStore';
import LoginModal from './LoginModal'; 

const HomeContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh; /* Ajustado para ocupar a altura total da tela */
  background-color: #121212;
  color: #fff;
  text-align: center;
  position: relative; /* Adicionado para posicionar o botão de login */
`;

const LoginButton = styled.button`
  position: absolute;
  top: 20px;
  right: 20px;
  background-color: #fff;
  color: #000;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
`;

const WelcomeMessage = styled.h1`
  margin-bottom: 20px;
`;

const ActionButton = styled.button`
  background-color: #5CB85C;
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin: 5px;
`;

const HomeContent: React.FC = () => {
  const { isAuthenticated } = useAuthStore();
  const [showLoginModal, setShowLoginModal] = React.useState(false);

  return (
    <HomeContentContainer>
      <LoginButton onClick={() => setShowLoginModal(true)}>Login</LoginButton>
      <WelcomeMessage>
        {isAuthenticated ? 'Bem-vindo de volta!' : 'Bem-vindo ao nosso site!'}
      </WelcomeMessage>
      {!isAuthenticated ? (
        <>
          <ActionButton>Criar Conta</ActionButton>
        </>
      ) : (
        <ActionButton>Ir para o Perfil</ActionButton>
      )}
      {showLoginModal && (
        <LoginModal onClose={() => setShowLoginModal(false)} onLogin={() => { /* Handle login here */ }} />
      )}
    </HomeContentContainer>
  );
};

export default HomeContent;
