import React, { useState } from 'react';
import styled from 'styled-components';
import { useAuthStore } from '../store/authStore';
import LoginModal from '../components/LoginModal';
import ProfileModal from '../components/ProfileModal';

const HomeContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: #121212;
  color: #fff;
  text-align: center;
`;

const WelcomeMessage = styled.h1`
  margin-bottom: 20px;
`;

const ActionButton = styled.button`
  background-color: #5CB85C;
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin: 5px;
`;

const HeaderContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  padding: 10px;
  background-color: #000;
  position: fixed;
  top: 0;
  width: 100%;
`;

const HeaderButton = styled.button`
  color: white;
  background: none;
  border: none;
  cursor: pointer;
`;

const Home: React.FC = () => {
  const { isAuthenticated, userId } = useAuthStore();
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [showProfileModal, setShowProfileModal] = useState(false);

  return (
    <HomeContainer>
      <HeaderContainer>
        {!isAuthenticated ? (
          <HeaderButton onClick={() => setShowLoginModal(true)}>Login</HeaderButton>
        ) : (
          <HeaderButton onClick={() => setShowProfileModal(true)}>Profile</HeaderButton>
        )}
      </HeaderContainer>

      <WelcomeMessage>
        {isAuthenticated ? 'Bem-vindo de volta!' : 'Bem-vindo ao nosso site!'}
      </WelcomeMessage>

      {!isAuthenticated && (
        <>
          <ActionButton>Criar Conta</ActionButton>
        </>
      )}
      
      {showLoginModal && <LoginModal onClose={() => setShowLoginModal(false)} onLogin={() => setShowLoginModal(false)} />}
      {showProfileModal && userId && <ProfileModal onClose={() => setShowProfileModal(false)} userId={userId} />}
    </HomeContainer>
  );
};

export default Home;
