import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import RegisterModal from './RegisterModal';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
`;

const LoginModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isRegisterModalOpen, setRegisterModalOpen] = useState(false);

  const handleLogin = async () => {
    try {
      await axios.post('http://localhost:5002/api/login', { email, password });
      alert('Login realizado com sucesso.');
      onClose();
    } catch (error) {
      alert('Erro ao fazer login.');
    }
  };

  const handleOpenRegisterModal = () => {
    setRegisterModalOpen(true);
  };

  const handleRegisterSuccess = () => {
    setRegisterModalOpen(false);
    onClose(); // Fecha o LoginModal ao registrar com sucesso
  };

  return (
    <>
      <ModalContainer>
        <ModalHeader>
          <ModalTitle>Login</ModalTitle>
          <CloseButton onClick={onClose}>X</CloseButton>
        </ModalHeader>
        <InputGroup>
          <Label>E-mail</Label>
          <Input value={email} onChange={(e) => setEmail(e.target.value)} />
        </InputGroup>
        <InputGroup>
          <Label>Senha</Label>
          <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </InputGroup>
        <ActionButton onClick={handleLogin}>Entrar</ActionButton>
        <p>
          Não tem uma conta? <a onClick={handleOpenRegisterModal}>Crie uma agora</a>
        </p>
      </ModalContainer>
      {isRegisterModalOpen && <RegisterModal onClose={() => setRegisterModalOpen(false)} onRegisterSuccess={handleRegisterSuccess} />}
    </>
  );
};

export default LoginModal;
