import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { login } from '../services/api'; 

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: black;
`;

const LoginBox = styled.div`
  background-color: black;
  border: 1px solid gray;
  padding: 20px;
  border-radius: 8px;
  width: 300px;
  color: white;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid gray;
  border-radius: 4px;
  background-color: #333;
  color: white;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin-top: 10px;
  &:hover {
    background: #0056b3;
  }
`;

const Link = styled.a`
  color: #007bff;
  cursor: pointer;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const Home: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = () => {
    // Lógica de login
    navigate('/users'); // Redireciona para a página de usuários após o login
  };

  return (
    <Container>
      <LoginBox>
        <h2>Acesse sua conta</h2>
        <Input
          
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button onClick={handleLogin}>Login</Button>
        <p>
          Não tem uma conta ainda? <Link onClick={() => navigate('/register')}>Criar conta</Link>
        </p>
      </LoginBox>
    </Container>
  );
};

export default Home;
