import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
`;

const RegisterModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleRegister = async () => {
    try {
      await axios.post('/api/users', { name, email, password });
      alert('Cadastro realizado com sucesso. Verifique seu e-mail para o token de confirmação.');
      onClose();
    } catch (error) {
      alert('Erro ao criar conta.');
    }
  };

  return (
    <ModalContainer>
      <ModalHeader>
        <ModalTitle>Crie sua conta</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      <InputGroup>
        <Label>Nome completo</Label>
        <Input value={name} onChange={(e) => setName(e.target.value)} />
      </InputGroup>
      <InputGroup>
        <Label>E-mail</Label>
        <Input value={email} onChange={(e) => setEmail(e.target.value)} />
      </InputGroup>
      <InputGroup>
        <Label>Senha</Label>
        <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      </InputGroup>
      <ActionButton onClick={handleRegister}>Cadastrar</ActionButton>
    </ModalContainer>
  );
};

export default RegisterModal;
