import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const HomeContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #000;
`;

const LoginBox = styled.div`
  background-color: #000;
  padding: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  text-align: center;
  color: white;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
  background-color: #222;
  color: #fff;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  &:hover {
    background: #0056b3;
  }
`;

const RegisterLink = styled.a`
  color: #007bff;
  cursor: pointer;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const ErrorMessage = styled.p`
  color: red;
`;

const Users: React.FC = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async () => {
    setError(''); // Limpar qualquer mensagem de erro anterior
    try {
      // Fazer a requisição para a API de login (substitua pela sua URL real)
      const response = await fetch('http://localhost:5000/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        // Se o login for bem-sucedido, redireciona para a página de usuários
        navigate('/users');
      } else {
        // Se o login falhar, verifica se o usuário não existe
        const data = await response.json();
        if (data.message === 'Usuário não encontrado') {
          setError('Usuário não existe');
        } else {
          setError('Falha no login. Verifique suas credenciais.');
        }
      }
    } catch (error) {
      setError('Erro ao conectar ao servidor.');
    }
  };

  return (
    <HomeContainer>
      <LoginBox>
        <h2>Login</h2>
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          type="password"
          placeholder="Senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Button onClick={handleLogin}>Login</Button>
        {error && <ErrorMessage>{error}</ErrorMessage>}
        <p>
          Não tem uma conta ainda?{' '}
          <RegisterLink onClick={() => navigate('/register')}>
            Criar conta.
          </RegisterLink>
        </p>
      </LoginBox>
    </HomeContainer>
  );
};

export default Home;
