import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { fetchUserById, updateUser } from '../services/api';
import { User } from '../types/user';

const UpdateUserContainer = styled.div`
  padding: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #ddd;
  border-radius: 4px;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  &:hover {
    background: #0056b3;
  }
`;

const UpdateUser: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [user, setUser] = useState<User | null>(null);

  useEffect(() => {
    const getUser = async () => {
      try {
        const result = await fetchUserById(Number(id));
        setUser(result);
      } catch (error) {
        console.error('Erro ao buscar usuário', error);
      }
    };

    getUser();
  }, [id]);

  const handleUpdate = async () => {
    if (user) {
      try {
        await updateUser(user.id, { name: user.name, email: user.email }); // Passa id e userData
        // Redirecionar para a página de usuários ou outra página
        window.location.href = '/users';
      } catch (error) {
        console.error('Erro ao atualizar usuário', error);
      }
    }
  };
  

  return (
    <UpdateUserContainer>
      <h2>Atualizar Usuário</h2>
      {user && (
        <div>
          <Input
            type="text"
            value={user.name}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
          />
          <Input
            type="email"
            value={user.email}
            onChange={(e) => setUser({ ...user, email: e.target.value })}
          />
          <Button onClick={handleUpdate}>Atualizar</Button>
        </div>
      )}
    </UpdateUserContainer>
  );
};

export default UpdateUser;
