// src/components/LoginModal.tsx
import React, { useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import RegisterModal from './RegisterModal';
import { useAuthStore } from '../store/authStore';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
`;

const Link = styled.a`
  color: #5CB85C;
  cursor: pointer;
  display: block;
  margin-top: 10px;
  text-align: center;
`;

const LoginModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showRegister, setShowRegister] = useState(false);
  const setAuthenticated = useAuthStore((state) => state.setAuthenticated);
  const setUserId = useAuthStore((state) => state.setUserId);
  const setEmailInStore = useAuthStore((state) => state.userId);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:5002/api/login', { email, password });
      setAuthenticated(true);
      setUserId(response.data.userId); // Se estiver usando userId
      setEmailInStore(response.data.email); // Se estiver usando email
      onClose();
    } catch (error) {
      alert('Erro ao fazer login.');
    }
  };

  return (
    <ModalContainer>
      <ModalHeader>
        <ModalTitle>{showRegister ? 'Crie sua conta' : 'Acesse sua conta'}</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      {!showRegister ? (
        <>
          <InputGroup>
            <Label>E-mail</Label>
            <Input value={email} onChange={(e) => setEmail(e.target.value)} />
          </InputGroup>
          <InputGroup>
            <Label>Senha</Label>
            <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </InputGroup>
          <ActionButton onClick={handleLogin}>Entrar</ActionButton>
          <Link onClick={() => setShowRegister(true)}>Ainda não é cadastrado? Crie sua conta.</Link>
        </>
      ) : (
        <RegisterModal onClose={() => setShowRegister(false)} />
      )}
    </ModalContainer>
  );
};

export default LoginModal;
