// src/pages/Home.tsx
import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { useAuthStore } from '../store/authStore';
import LoginModal from '../components/LoginModal';
import ProfileModal from '../components/ProfileModal';

const HomeContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 80vh; /* Ajuste conforme necessário */
  background-color: #121212;
  color: #fff;
  text-align: center;
`;

const WelcomeMessage = styled.h1`
  margin-bottom: 20px;
`;

const ActionButton = styled(Link)`
  background-color: #5CB85C;
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin: 5px;
  text-decoration: none;
  display: inline-block;
  text-align: center;
`;

const HomeContent: React.FC = () => {
  const { isAuthenticated } = useAuthStore();

  return (
    <HomeContentContainer>
      <WelcomeMessage>
        {isAuthenticated ? 'Bem-vindo de volta!' : 'Bem-vindo ao nosso site!'}
      </WelcomeMessage>
      {!isAuthenticated ? (
        <>
          <ActionButton to="/login">Login</ActionButton>
          <ActionButton to="/register">Criar Conta</ActionButton>
        </>
      ) : (
        <ActionButton to="/profile">Ir para o Perfil</ActionButton>
      )}
    </HomeContentContainer>
  );
};

export default HomeContent;
