// src/components/ProfileModal.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuthStore } from '../store/authStore';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
`;

const ProfileModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const [userInfo, setUserInfo] = useState<{ name: string; email: string } | null>(null);
  const userId = useAuthStore((state) => state.userId);

  useEffect(() => {
    const fetchUserInfo = async () => {
      try {
        console.log(`ProfileModal opened, userId: ${userId}`); // Debug log
        if (userId) {
          const response = await axios.get(`http://localhost:5002/api/users/${userId}`);
          setUserInfo(response.data);
        } else {
          console.log('userId is null or undefined'); // Debug log
        }
      } catch (error) {
        alert('Erro ao carregar informações do usuário.');
      }
    };

    fetchUserInfo();
  }, [userId]);

  const handleUpdate = async () => {
    try {
      if (userInfo) {
        await axios.put(`/api/users/${userId}`, userInfo);
        alert('Informações atualizadas com sucesso.');
        onClose();
      }
    } catch (error) {
      alert('Erro ao atualizar informações.');
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/users/${userId}`);
      alert('Conta deletada com sucesso.');
      onClose();
    } catch (error) {
      alert('Erro ao deletar conta.');
    }
  };

  if (!userInfo) return null;

  return (
    <ModalContainer>
      // src/components/ProfileModal.tsx
      <ModalHeader>
        <ModalTitle>Profile</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      <InputGroup>
        <Label htmlFor="name">Name</Label>
        <Input
          id="name"
          type="text"
          value={userInfo.name}
          onChange={(e) => setUserInfo({ ...userInfo, name: e.target.value })}
        />
      </InputGroup>
      <InputGroup>
        <Label htmlFor="email">Email</Label>
        <Input
          id="email"
          type="email"
          value={userInfo.email}
          onChange={(e) => setUserInfo({ ...userInfo, email: e.target.value })}
        />
      </InputGroup>
      <ActionButton onClick={handleUpdate}>Update Profile</ActionButton>
      <ActionButton onClick={handleDelete} style={{ backgroundColor: '#d9534f' }}>
        Delete Account
      </ActionButton>
    </ModalContainer>
  );
};

export default ProfileModal;
