import React from 'react';
import styled from 'styled-components';

const HomeContentContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #121212;
  color: #fff;
  text-align: center;
`;

const WelcomeMessage = styled.h1`
  margin-bottom: 20px;
`;

const ActionButton = styled.button`
  background-color: #5CB85C;
  color: #fff;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  margin: 5px;
`;

const MovieContainer = styled.div`
  width: 80%;
  margin: 20px auto;
`;

const MovieImage = styled.img`
  width: 100%;
  max-width: 600px;
  border-radius: 8px;
`;

const MovieDescription = styled.p`
  color: #fff;
  margin-top: 10px;
`;

const HomeContent: React.FC = () => {
  
  return (
    <HomeContentContainer>
      <WelcomeMessage>Bem-vindo ao nosso site!</WelcomeMessage>
      {/* Exemplo de conteúdo de filmes */}
      <MovieContainer>
        <MovieImage src="./img/wolverinedeadpool.webp" alt="Movie Title" />
        <MovieDescription>Sinopse do filme aqui...</MovieDescription>
      </MovieContainer>
      {/* Botões de ação, se necessário */}
    </HomeContentContainer>
  );
};

export default HomeContent;
