// src/components/ProfileModal.tsx
import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuthStore } from '../store/authStore';
import { deleteUser, updateUser } from '../services/api'; // Ajuste se necessário

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 12px;
  border: 1px solid #444;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 10px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: calc(100% - 22px);
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
  text-align: center; /* Alinha o texto ao centro */
  margin-bottom: 10px;
`;

const ActionButton = styled.button`
  padding: 8px 12px;
  border: none;
  border-radius: 4px;
  color: #fff;
  cursor: pointer;
  font-size: 14px;
  margin-top: 10px;
`;

const SaveButton = styled(ActionButton)`
  background-color: #fff;
  color: #333;
  position: absolute;
  bottom: 10px;
  right: 10px;
`;

const DeleteButton = styled(ActionButton)`
  background-color: #d9534f;
  position: absolute;
  bottom: 10px;
  left: 10px;
`;

const ProfileModal: React.FC<{ onClose: () => void; closeTab: () => void }> = ({ onClose, closeTab }) => {
  const [userInfo, setUserInfo] = useState<{ name: string; email: string } | null>(null);
  const userId = useAuthStore((state) => state.userId);

  useEffect(() => {
    if (!userId) return;

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get(`http://localhost:5002/api/users/${userId}`);
        setUserInfo(response.data);
      } catch (error) {
        alert('Erro ao carregar informações do usuário.');
      }
    };

    fetchUserInfo();
  }, [userId]);

  const handleSave = async () => {
    if (userInfo) {
      try {
        await updateUser(userId, userInfo); // Ajuste se necessário
        alert('Informações atualizadas com sucesso.');
        closeTab(); // Fecha a aba ao salvar
      } catch (error) {
        alert('Erro ao atualizar informações.');
      }
    }
  };

  const handleDelete = async () => {
    try {
      await deleteUser(); // Ajuste se necessário
      alert('Conta deletada com sucesso.');
      onClose(); // Fecha o modal ao deletar
      closeTab(); // Fecha a aba ao deletar
    } catch (error) {
      alert('Erro ao deletar conta.');
    }
  };

  if (!userInfo) return null;

  return (
    <ModalContainer>
      <ModalHeader>
        <ModalTitle>Seu Perfil</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      <InputGroup>
        <Label>Nome</Label>
        <Input
          value={userInfo.name}
          onChange={(e) => setUserInfo((prev) => prev ? { ...prev, name: e.target.value } : prev)}
        />
      </InputGroup>
      <InputGroup>
        <Label>E-mail</Label>
        <Input
          value={userInfo.email}
          readOnly
        />
      </InputGroup>
      <SaveButton onClick={handleSave}>Salvar alterações</SaveButton>
      <DeleteButton onClick={handleDelete}>Deletar</DeleteButton>
    </ModalContainer>
  );
};

export default ProfileModal;
