import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useAuthStore } from '../store/authStore';

const ModalContainer = styled.div`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  background-color: #121212;
  padding: 20px;
  border-radius: 8px;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.3);
  width: 400px;
  z-index: 1000;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const ModalTitle = styled.h2`
  color: #fff;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
`;

const InputGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  color: #fff;
  margin-bottom: 5px;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  border-radius: 4px;
  border: 1px solid #444;
  background-color: #333;
  color: #fff;
`;

const ActionButton = styled.button`
  width: 100%;
  padding: 10px;
  border: none;
  border-radius: 4px;
  background-color: #5CB85C;
  color: #fff;
  cursor: pointer;
  margin-bottom: 10px;
`;

const ConfirmDeleteContainer = styled.div`
  background-color: #222;
  padding: 20px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const ConfirmDeleteText = styled.p`
  color: #fff;
  margin-bottom: 20px;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: space-between;
`;

const DeleteButton = styled.button`
  background-color: #d9534f;
  color: #fff;
  padding: 10px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const CancelButton = styled.button`
  background-color: #5CB85C;
  color: #fff;
  padding: 10px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
`;

const ProfileModal: React.FC<{ onClose: () => void }> = ({ onClose }) => {
  const [userInfo, setUserInfo] = useState<{ name: string; email: string } | null>(null);
  const [showConfirmDelete, setShowConfirmDelete] = useState(false);
  const userId = useAuthStore((state) => state.userId);
  const setAuthenticated = useAuthStore((state) => state.setAuthenticated);
  const setUserId = useAuthStore((state) => state.setUserId);

  useEffect(() => {
    const fetchUserInfo = async () => {
      if (!userId) {
        console.log('userId is null or undefined');
        return;
      }

      try {
        const response = await axios.get(`http://localhost:5002/api/users/${userId}`);
        setUserInfo(response.data);
      } catch (error) {
        alert('Erro ao carregar informações do usuário.');
      }
    };

    fetchUserInfo();
  }, [userId]);

  const handleUpdate = async () => {
    if (userInfo && userId) {
      try {
        await axios.put(`http://localhost:5002/api/users/${userId}`, userInfo);
        alert('Usuário atualizado com sucesso');
        onClose(); // Fechar o modal após salvar as alterações
      } catch (error) {
        alert('Erro ao atualizar usuário.');
      }
    }
  };

  const handleDelete = async () => {
    if (userId) {
      try {
        await axios.delete(`http://localhost:5002/api/users/${userId}`);
        alert('Usuário deletado com sucesso');
        setAuthenticated(false);
        setUserId(null);
        onClose(); // Fechar o modal após exclusão
      } catch (error) {
        alert('Erro ao excluir usuário.');
      }
    }
  };

  if (!userInfo) return null;

  return (
    <ModalContainer>
      <ModalHeader>
        <ModalTitle>Seu Perfil</ModalTitle>
        <CloseButton onClick={onClose}>X</CloseButton>
      </ModalHeader>
      <InputGroup>
        <Label>Nome completo:</Label>
        <Input
          value={userInfo.name}
          onChange={(e) => setUserInfo((prev) => prev ? { ...prev, name: e.target.value } : prev)}
        />
      </InputGroup>
      <InputGroup>
        <Label>E-mail:</Label>
        <Input
          value={userInfo.email}
          onChange={(e) => setUserInfo((prev) => prev ? { ...prev, email: e.target.value } : prev)}
        />
      </InputGroup>
      <ActionButton onClick={handleUpdate}>Salvar alterações</ActionButton>
      <ActionButton onClick={() => setShowConfirmDelete(true)} style={{ backgroundColor: '#d9534f' }}>Deletar</ActionButton>
      
      {showConfirmDelete && (
        <ConfirmDeleteContainer>
          <ConfirmDeleteText>Tem certeza que deseja deletar sua conta?</ConfirmDeleteText>
          <ButtonGroup>
            <DeleteButton onClick={handleDelete}>Deletar</DeleteButton>
            <CancelButton onClick={() => setShowConfirmDelete(false)}>Cancelar</CancelButton>
          </ButtonGroup>
        </ConfirmDeleteContainer>
      )}
    </ModalContainer>
  );
};

export default ProfileModal;
