import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { login } from '../services/api';

const HomeContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #000;
`;

const LoginBox = styled.div`
  background-color: #000;
  padding: 20px;
  border: 1px solid #444; /* Borda cinza mais escura */
  border-radius: 5px;
  text-align: center;
  color: white;
  width: 300px; /* Ajusta a largura da caixa */
`;

const Label = styled.label`
  display: block;
  margin: 10px 0 5px;
  color: #ccc; /* Cor dos rótulos */
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  margin: 10px 0;
  border: 1px solid #666; /* Borda cinza mais escura */
  border-radius: 4px;
  background-color: #222;
  color: #fff;
`;

const Button = styled.button`
  width: 100%;
  padding: 10px;
  background: #007bff;
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;
  &:hover {
    background: #0056b3;
  }
`;

const RegisterLink = styled.a`
  color: #fff; /* Link branco */
  cursor: pointer;
  text-decoration: none;
  &:hover {
    text-decoration: underline;
  }
`;

const ErrorMessage = styled.p`
  color: red;
  margin: 10px 0;
`;

const InfoText = styled.p`
  color: #ccc; /* Texto em cor cinza mais clara */
  font-size: 14px; /* Tamanho da fonte menor */
  margin-bottom: 10px;
`;

const Home: React.FC = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await login(email, password);
      console.log('Login response:', response); 
      if (response.token) {
        navigate('/users');
      }
    } catch (error) {
      console.error('Login error:', error);
      setErrorMessage('Email ou senha incorretos');
    }
  };

  return (
    <HomeContainer>
      <LoginBox>
        <h2>Acesse sua conta</h2>
        <InfoText>Bem-vindo de volta! Entre com seus dados.</InfoText>
        
        <Label htmlFor="email">E-mail</Label>
        <Input
          id="email"
          type="email"
          placeholder="Digite seu email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        
        <Label htmlFor="password">Senha</Label>
        <Input
          id="password"
          type="password"
          placeholder="Digite sua senha"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        
        {errorMessage && <ErrorMessage>{errorMessage}</ErrorMessage>}
        
        <Button onClick={handleLogin}>Login</Button>
        
        
        <p>
          Não tem uma conta ainda?{' '}
          <RegisterLink onClick={() => navigate('/register')}>
            Criar conta.
          </RegisterLink>
        </p>
      </LoginBox>
    </HomeContainer>
  );
};

export default Home;
