// src/store/authStore.ts

import create from 'zustand';

interface AuthState {
  isAuthenticated: boolean;
  userId: string | null;
  email: string | null;  // Adicione esta linha
  setAuthenticated: (authenticated: boolean, email?: string, userId?: string) => void;
  setUserId: (userId: string | null) => void;
  setEmail: (email: string | null) => void;  // Adicione esta linha
}

export const useAuthStore = create<AuthState>((set) => ({
  isAuthenticated: false,
  userId: null,
  email: null,  // Adicione esta linha
  setAuthenticated: (authenticated, email = null, userId = null) => set({ isAuthenticated: authenticated, email, userId }),
  setUserId: (userId) => set({ userId }),
  setEmail: (email) => set({ email }),  // Adicione esta linha
}));
