// src/components/ProfileModal.tsx

import React, { useEffect, useState } from 'react';
import { useAuthStore } from '../store/authStore';

interface ProfileModalProps {
  onClose: () => void;
  userInfo?: { name: string; email: string };  // Adicione a propriedade userInfo
}

const ProfileModal: React.FC<ProfileModalProps> = ({ onClose, userInfo }) => {
  const [localUserInfo, setLocalUserInfo] = useState<{ name: string; email: string } | null>(userInfo || null);

  useEffect(() => {
    setLocalUserInfo(userInfo || null);  // Atualize o estado local se userInfo mudar
  }, [userInfo]);

  return (
    <div>
      {/* Seu código para renderizar o modal */}
    </div>
  );
};

export default ProfileModal;
