// src/store/authStore.ts

import create from 'zustand';

interface AuthState {
  isAuthenticated: boolean;
  userId: string | null;  // Permite null
  email: string | null;   // Permite null
  setAuthenticated: (authenticated: boolean, email?: string | null, userId?: string | null) => void;
  setUserId: (userId: string | null) => void;
  setEmail: (email: string | null) => void;
}

export const useAuthStore = create<AuthState>((set) => ({
  isAuthenticated: false,
  userId: null,          // Permite null
  email: null,           // Permite null
  setAuthenticated: (authenticated, email = null, userId = null) => set({ isAuthenticated: authenticated, email, userId }),
  setUserId: (userId) => set({ userId }),
  setEmail: (email) => set({ email }),
}));
